import type { Bot, TaskState } from "@shared/schema";

export interface IStorage {
  // Task states
  getTaskState(taskName: string): Promise<TaskState>;
  updateTaskState(taskName: string, state: Partial<TaskState>): Promise<TaskState>;

  // Bot management
  getBots(): Promise<Bot[]>;
  addBot(bot: Bot): Promise<Bot>;
  updateBot(id: string, updates: Partial<Bot>): Promise<Bot | undefined>;
  clearBots(): Promise<void>;

  // Membership results
  getMemberBots(): Promise<Bot[]>;
  getNonMemberBots(): Promise<Bot[]>;
  setMembershipResults(members: Bot[], nonMembers: Bot[]): Promise<void>;
}

export class MemStorage implements IStorage {
  private taskStates: Map<string, TaskState>;
  private bots: Map<string, Bot>;
  private memberBots: Bot[];
  private nonMemberBots: Bot[];

  constructor() {
    this.taskStates = new Map();
    this.bots = new Map();
    this.memberBots = [];
    this.nonMemberBots = [];
  }

  async getTaskState(taskName: string): Promise<TaskState> {
    return this.taskStates.get(taskName) || {
      status: 'idle',
      progress: { completed: 0, processing: 0, failed: 0, total: 0 },
      bots: [],
    };
  }

  async updateTaskState(taskName: string, state: Partial<TaskState>): Promise<TaskState> {
    const current = await this.getTaskState(taskName);
    const updated = { ...current, ...state };
    this.taskStates.set(taskName, updated);
    return updated;
  }

  async getBots(): Promise<Bot[]> {
    return Array.from(this.bots.values());
  }

  async addBot(bot: Bot): Promise<Bot> {
    this.bots.set(bot.id, bot);
    return bot;
  }

  async updateBot(id: string, updates: Partial<Bot>): Promise<Bot | undefined> {
    const bot = this.bots.get(id);
    if (!bot) return undefined;
    const updated = { ...bot, ...updates };
    this.bots.set(id, updated);
    return updated;
  }

  async clearBots(): Promise<void> {
    this.bots.clear();
  }

  async getMemberBots(): Promise<Bot[]> {
    return this.memberBots;
  }

  async getNonMemberBots(): Promise<Bot[]> {
    return this.nonMemberBots;
  }

  async setMembershipResults(members: Bot[], nonMembers: Bot[]): Promise<void> {
    this.memberBots = members;
    this.nonMemberBots = nonMembers;
  }
}

export const storage = new MemStorage();

