import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import type { Bot } from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  // Messages Task
  app.post("/api/messages/start", async (req, res) => {
    try {
      const memberBots = await storage.getMemberBots();
      await storage.updateTaskState('messages', {
        status: 'running',
        progress: { completed: 0, processing: memberBots.length, failed: 0, total: memberBots.length },
        bots: memberBots,
      });
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ error: 'Failed to start messages task' });
    }
  });

  app.post("/api/messages/stop", async (req, res) => {
    try {
      const current = await storage.getTaskState('messages');
      await storage.updateTaskState('messages', { status: 'stopped' });
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ error: 'Failed to stop messages task' });
    }
  });

  app.get("/api/messages/status", async (req, res) => {
    try {
      const state = await storage.getTaskState('messages');
      res.json(state);
    } catch (error) {
      res.status(500).json({ error: 'Failed to get messages status' });
    }
  });

  // Mic Task
  app.post("/api/mic/start", async (req, res) => {
    try {
      const memberBots = await storage.getMemberBots();
      await storage.updateTaskState('mic', {
        status: 'running',
        progress: { completed: 0, processing: memberBots.length, failed: 0, total: memberBots.length },
        bots: memberBots,
      });
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ error: 'Failed to start mic task' });
    }
  });

  app.post("/api/mic/stop", async (req, res) => {
    try {
      await storage.updateTaskState('mic', { status: 'stopped' });
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ error: 'Failed to stop mic task' });
    }
  });

  app.get("/api/mic/status", async (req, res) => {
    try {
      const state = await storage.getTaskState('mic');
      res.json(state);
    } catch (error) {
      res.status(500).json({ error: 'Failed to get mic status' });
    }
  });

  // Name Change Task
  app.post("/api/name-change/start", async (req, res) => {
    try {
      const { names } = req.body;
      if (!names || !Array.isArray(names) || names.length === 0) {
        return res.status(400).json({ error: 'Names list is required' });
      }

      const memberBots = await storage.getMemberBots();
      await storage.updateTaskState('nameChange', {
        status: 'running',
        progress: { completed: 0, processing: names.length, failed: 0, total: names.length },
        bots: memberBots,
      });
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ error: 'Failed to start name change' });
    }
  });

  app.post("/api/name-change/stop", async (req, res) => {
    try {
      await storage.updateTaskState('nameChange', { status: 'stopped' });
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ error: 'Failed to stop name change' });
    }
  });

  app.get("/api/name-change/status", async (req, res) => {
    try {
      const state = await storage.getTaskState('nameChange');
      res.json(state);
    } catch (error) {
      res.status(500).json({ error: 'Failed to get name change status' });
    }
  });

  // Loader Task
  app.post("/api/loader/connect", async (req, res) => {
    try {
      const { clubCode, botCount } = req.body;
      if (!clubCode) {
        return res.status(400).json({ error: 'Club code is required' });
      }

      const allBots = await storage.getBots();
      const botsToConnect = botCount ? allBots.slice(0, parseInt(botCount)) : allBots;

      await storage.updateTaskState('loader', {
        status: 'running',
        progress: { completed: 0, processing: botsToConnect.length, failed: 0, total: botsToConnect.length },
        bots: botsToConnect,
      });
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ error: 'Failed to connect bots' });
    }
  });

  app.post("/api/loader/disconnect", async (req, res) => {
    try {
      await storage.updateTaskState('loader', { status: 'stopped' });
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ error: 'Failed to disconnect bots' });
    }
  });

  app.get("/api/loader/status", async (req, res) => {
    try {
      const state = await storage.getTaskState('loader');
      res.json(state);
    } catch (error) {
      res.status(500).json({ error: 'Failed to get loader status' });
    }
  });

  // Membership Check
  app.post("/api/membership/check", async (req, res) => {
    try {
      const { groupLink, tokens } = req.body;
      if (!groupLink || !tokens || !Array.isArray(tokens) || tokens.length === 0) {
        return res.status(400).json({ error: 'Group link and tokens are required' });
      }

      // Simulate membership check (in real implementation, this would call Telegram API)
      const members: Bot[] = [];
      const nonMembers: Bot[] = [];

      tokens.forEach((token: string, index: number) => {
        const bot: Bot = {
          id: `bot_${index}`,
          name: `bot_${index + 1}`,
          token: token,
          status: Math.random() > 0.5 ? 'member' : 'non-member',
        };

        if (bot.status === 'member') {
          members.push(bot);
        } else {
          nonMembers.push(bot);
        }
      });

      await storage.setMembershipResults(members, nonMembers);
      await storage.updateTaskState('membership', {
        status: 'idle',
        progress: { completed: tokens.length, processing: 0, failed: 0, total: tokens.length },
        bots: [...members, ...nonMembers],
      });

      res.json({ 
        success: true, 
        members: members.length, 
        nonMembers: nonMembers.length 
      });
    } catch (error) {
      res.status(500).json({ error: 'Failed to check membership' });
    }
  });

  app.get("/api/membership/results", async (req, res) => {
    try {
      const members = await storage.getMemberBots();
      const nonMembers = await storage.getNonMemberBots();
      res.json({ members, nonMembers });
    } catch (error) {
      res.status(500).json({ error: 'Failed to get membership results' });
    }
  });

  // Load tokens from file (stub)
  app.post("/api/tokens/upload", async (req, res) => {
    try {
      const { tokens } = req.body;
      if (!tokens || !Array.isArray(tokens)) {
        return res.status(400).json({ error: 'Tokens array is required' });
      }
      res.json({ success: true, count: tokens.length });
    } catch (error) {
      res.status(500).json({ error: 'Failed to upload tokens' });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

