@tailwind base;
@tailwind components;
@tailwind utilities;

/* LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -8; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  --background: 0 0% 100%;

  --foreground: 222 47% 11%;

  --border: 214 32% 91%;

  --card: 0 0% 98%;

  --card-foreground: 222 47% 11%;

  --card-border: 214 32% 91%;

  --sidebar: 0 0% 96%;

  --sidebar-foreground: 222 47% 11%;

  --sidebar-border: 214 32% 88%;

  --sidebar-primary: 221 83% 53%;

  --sidebar-primary-foreground: 210 40% 98%;

  --sidebar-accent: 220 13% 91%;

  --sidebar-accent-foreground: 222 47% 11%;

  --sidebar-ring: 221 83% 53%;

  --popover: 0 0% 94%;

  --popover-foreground: 222 47% 11%;

  --popover-border: 214 32% 86%;

  --primary: 221 83% 53%;

  --primary-foreground: 210 40% 98%;

  --secondary: 220 13% 88%;

  --secondary-foreground: 222 47% 11%;

  --muted: 220 13% 91%;

  --muted-foreground: 215 16% 47%;

  --accent: 189 94% 43%;

  --accent-foreground: 189 94% 15%;

  --destructive: 0 84% 60%;

  --destructive-foreground: 0 0% 98%;

  --input: 214 32% 80%;
  --ring: 221 83% 53%;
  --chart-1: 221 83% 45%;
  --chart-2: 142 76% 36%;
  --chart-3: 262 83% 50%;
  --chart-4: 38 92% 50%;
  --chart-5: 189 94% 43%;

  --font-sans: Inter, -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen', 'Ubuntu', sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: 'Fira Code', 'Monaco', 'Courier New', monospace;
  --radius: .75rem;
  --shadow-2xs: 0px 1px 2px 0px hsl(222 47% 11% / 0.05);
  --shadow-xs: 0px 1px 3px 0px hsl(222 47% 11% / 0.1), 0px 1px 2px 0px hsl(222 47% 11% / 0.06);
  --shadow-sm: 0px 2px 4px -1px hsl(222 47% 11% / 0.06), 0px 4px 6px -1px hsl(222 47% 11% / 0.1);
  --shadow: 0px 4px 6px -2px hsl(222 47% 11% / 0.05), 0px 10px 15px -3px hsl(222 47% 11% / 0.1);
  --shadow-md: 0px 6px 12px -2px hsl(222 47% 11% / 0.08), 0px 12px 20px -4px hsl(222 47% 11% / 0.12);
  --shadow-lg: 0px 10px 24px -4px hsl(222 47% 11% / 0.1), 0px 20px 32px -8px hsl(222 47% 11% / 0.15);
  --shadow-xl: 0px 20px 40px -8px hsl(222 47% 11% / 0.15), 0px 32px 64px -12px hsl(222 47% 11% / 0.2);
  --shadow-2xl: 0px 32px 64px -12px hsl(222 47% 11% / 0.25);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

